import React, {useState} from 'react';

const Calculator = () => {
    const operators = ['-', '+', 'x', 'รท', '='];
    const numbers = [];
    const [memory, setMemory] = useState('');

    for (let i = 0; i < 10; i++) {
        numbers.push(i);
    }

    const updateMemory = (event) => {
        setMemory(memory + event.target.innerText);
    };

    const clearMemory = () => {
        setMemory('');
    };

    const calc = () => {
        setMemory(eval(memory));
    }

    return (
        <div className='calculator'>
            <div className='display'>{memory}</div>
            <div className='keypad'>
                <div className='key clear' onClick={clearMemory}>C</div>
                {numbers.map(number => (
                    <button onClick={updateMemory}
                            key={number} className='key'>
                        {number}
                    </button>
                ))}
                {operators.map(operator => (
                    <button onClick={operator !== '=' ? updateMemory : calc}
                            key={operator} className='key'>
                        {operator}
                    </button>
                ))}
            </div>
        </div>
    );
}

export default Calculator;
